const initState = {
    filters: {
        search: '',
        status: 'All',
        priority: []
    },
    todoList: [
        { id: 1, name: 'Learn React', completed: false, priority: 'Medium' },
        { id: 2, name: 'Learn JavaScript', completed: true, priority: 'Hight' },
        { id: 3, name: 'Learn NodeJs', completed: true, priority: 'Low' },
        { id: 4, name: 'Learn Reponsize', completed: false, priority: 'Medium' },
    ]
}
const rootReducer = (state = initState, action) => {
    /*
        {
            type: 'todoList/ADD_TODO',
            payload: {
                id: 5,
                name: 'Learn Footbal',
                completed: false,
                priority: 'Medium'
            }
        }
    */ 
    switch (action.type) {
        case 'todoList/ADD_TODO':
           return {
                ...state,
                todoList: [
                    ...state.todoList,
                    {
                        id: 5,
                        name: 'Learn Footbal',
                        completed: false,
                        priority: 'Medium'
                    }
                ]
           }
        default:
            return state;
    }
}

export default rootReducer;
